mapper.select:
select t1.id id, t1.car_number carNumber, t1.driver_name driverName, t1.driver_phone driverPhone, t1.follower_name followerName, t1.follower_phone followerPhone,t1.port_id portId, t1.port_name portName, t1.hotel_id otelId, t1.hotel_name hotelName, t1.departure_time departureTime, t1.arrive_time arriveTime, t1.status status, ifnull(t3.count, 0) travelerCount from entry_train t1 left join (select count(1) count, t2.train_id train_id from entry_traveler t2 group by t2.train_id) t3 on t3.train_id = t1.id where 1 = 1
and t1.car_number like concat('%', #{carNumber}, '%')
order by t1.update_time desc limit #{start},#{pageSize}

select t1.id id, t1.car_number carNumber, t1.driver_name driverName, t1.driver_phone driverPhone, t1.follower_name followerName, t1.follower_phone followerPhone,t1.port_id portId, t1.port_name portName, t1.hotel_id otelId, t1.hotel_name hotelName, t1.departure_time departureTime, t1.arrive_time arriveTime, t1.status status, ifnull(t3.count, 0) travelerCount from entry_train t1 left join (select count(1) count, t2.train_id train_id from entry_traveler t2 group by t2.train_id) t3 on t3.train_id = t1.id where t1.status = 'ARRIVED'
and t1.car_number like concat('%', #{carNumber}, '%')
and t1.hotel_id = #{hotelId}
and t1.arrive_time between #{startTime} and #{endTime}
order by t1.update_time desc limit #{start},#{pageSize}
